            function validateDate($date, $format = 'Y-m-d'){
                $d = DateTime::createFromFormat($format, $date);
                return $d && $d->format($format) === $date;
            }

            $date = $_POST["date"];
            $death = '18-05-2008';

            $time1 = strtotime($date);
            $time2 = strtotime($death);

            if(validateDate($date,'d-m-Y') && $time2 > $time1){
